     , TRIP_REPLY_DATE
     , TRIP_REVIEW_CODE
   )
   VALUES
   (
       #{ guideReplyCode }
      , #{ replyDetail }
      , NOW()
      , #{ reviewCode } 
  )

mapper.update:
